package Days_0212;import java.io.Serializable;/** * Created by admin on 2020/2/12. */public class Student_1 implements Serializable {    private int age;    private String name;    private String hisClass;    @Override    public String toString() {        return "Student_1{" +                "age=" + age +                ", name='" + name + '\'' +                ", hisClass='" + hisClass + '\'' +                '}';    }    public Student_1() {    }    public Student_1(int age, String name, String hisClass) {        this.age = age;        this.name = name;        this.hisClass = hisClass;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getHisClass() {        return hisClass;    }    public void setHisClass(String hisClass) {        this.hisClass = hisClass;    }}