package com.neuedu.test;/** * Created by admin on 2020/2/3. */public class Dog {    private char name;    private int age;    private char sec;    public char getName() {        return name;    }    public void setName(char name) {            this.name = name;    }    public int getAge() {        return age;    }    public void setAge(int age) {        if (age<0 || age>50){            System.out.println("年龄输入错误");        }else{            this.age=age;        }    }    public char getSec() {        return sec;    }    public void setSec(char sec) {        if (sec != '公'&& sec !='母'){            System.out.println("输入错误");        }else{            this.sec = sec;        }    }    public static int add(int a,int b){        int c=a+b;        return c;    }}